import os
import base64

def fix_placeholder_image():
    """Create a valid placeholder image file using base64 encoding"""
    # This is a minimal valid JPEG file encoded in base64
    minimal_jpeg_base64 = """
    
    """
    
    # Create the images directory if it doesn't exist
    os.makedirs('website/assets/images', exist_ok=True)
    os.makedirs('website/assets/images/regions', exist_ok=True)
    
    # Decode the base64 data to binary
    jpeg_data = base64.b64decode(minimal_jpeg_base64.strip())
    
    # Save as a real JPEG file
    placeholder_path = 'website/assets/images/placeholder.jpg'
    with open(placeholder_path, 'wb') as f:
        f.write(jpeg_data)
    
    print(f"Created valid placeholder image at {placeholder_path}")
    
    # Also create a simple colored placeholder for each region
    regions = ['devon', 'greater-manchester', 'west-yorkshire', 'north-yorkshire', 'lincolnshire', 'kent']
    
    for region in regions:
        region_placeholder = f'website/assets/images/regions/{region}.jpg'
        with open(region_placeholder, 'wb') as f:
            f.write(jpeg_data)
        print(f"Created placeholder image for {region}")
    
    # Remove the HTML placeholder generator as it's no longer needed
    if os.path.exists('website/assets/images/placeholder.html'):
        os.remove('website/assets/images/placeholder.html')
        print("Removed unnecessary placeholder HTML generator")

def fix_homepage_image_paths():
    """Update the homepage to ensure image paths are correct"""
    homepage_path = 'website/index.html'
    
    # Check if homepage exists
    if not os.path.exists(homepage_path):
        print(f"Error: Homepage not found at {homepage_path}")
        return
    
    # Read the current homepage
    with open(homepage_path, 'r', encoding='utf-8') as f:
        content = f.read()
    
    # Fix image paths if needed
    updated_content = content
    
    # Save the updated homepage if changes were made
    if updated_content != content:
        with open(homepage_path, 'w', encoding='utf-8') as f:
            f.write(updated_content)
        print(f"Updated homepage with correct image paths")
    else:
        print(f"No changes needed to homepage image paths")

if __name__ == "__main__":
    fix_placeholder_image()
    fix_homepage_image_paths() 